    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}
.addBtn button {
    transition: .2s;
    display: flex;
    margin: 5px auto;
    color: var(--global-bg);
    padding: 15px;
    border-radius: 40px;
    background: var(--search-result-title);
    align-items: center;
    background-image: url('https://pic3.zhimg.com/v2-0cc7372362ff95755673557f87149f52_r.jpg');
    background-position: center;
    background-size: cover;
    color: aquamarine;
}

button {
    padding: 0;
    outline: 0;
    border: none;
    background: 0 0;
    cursor: pointer;
    touch-action: manipulation;
    background-color: #888;
}
